const Offer = () => {
  return (
    <div className="offer">
      <div className="offer-left">
        <h1>EXCLUSIVE</h1>
        <h1>Offers For You </h1>
        <p>ONLY ON BEST SELLER PRODUCT </p>
        <Link
          to="/Clothes"
          style={{
            textDecoration: "none",
            color: "#eaeaea",
            fontweight: "1000",
          }}
        >
          {" "}
          <button>
            Check now <img src={arrow_icon} />{" "}
          </button>
        </Link>
      </div>
      <div className="offer-right">
        <img src={exclusive_img} alt="img" />
      </div>
    </div>
  );
};

export default Offer;
