import React, { useState, useContext, useRef } from "react";
import { Link } from "react-router-dom";
import logo_png from "../imgss/icone2.png";
import Cart_icon from "../imgss/cart.png";
import "./Navbar.css";
import { ShopContext } from "../../Context/ShopContext";
import dropdown from "../Assets/dropdown.png";

const Navbar = () => {
  const [menu, setMenu] = useState("home");

  const { getTotalCartItems } = useContext(ShopContext);
  const menuref = useRef();
  const dropdown_toggle = (e) => {
    menuref.current.classList.toggle("nav-item-visible");
    e.target.classList;
  };

  return (
    <>
      <div className="navbar">
        <div className="logo">
          <Link to="/">
            <img
              src={logo_png}
              alt=" "
              onClick={() => {
                setMenu("home");
              }}
            />
          </Link>
          <p>ShOppi</p>
        </div>
        <img src={dropdown} alt="" className="dropdown" />
        <ul ref={menuref} className="nav-items">
          <li
            onClick={() => {
              setMenu("home");
            }}
          >
            <Link
              style={{
                textDecoration: "none",
                color: "#eaeaea",
                fontWeight: "500",
              }}
              to="/"
            >
              HOME
            </Link>
            {menu === "home" ? <hr /> : <></>}
          </li>
          <li
            onClick={() => {
              setMenu("Clothes");
            }}
          >
            <Link
              to="/Clothes"
              style={{
                textDecoration: "none",
                color: "#eaeaea",
                fontweight: "1000",
              }}
            >
              CLOTHES
            </Link>
            {menu === "Clothes" ? <hr /> : <></>}
          </li>
          <li
            onClick={() => {
              setMenu("Watches");
            }}
          >
            <Link
              style={{
                textDecoration: "none",
                color: "#eaeaea",
                fontweight: "1000",
              }}
              to="/Watches"
            >
              WATCHES
            </Link>
            {menu === "Watches" ? <hr /> : <></>}
          </li>
          <li
            onClick={() => {
              setMenu("About");
            }}
          >
            <Link
              style={{
                textDecoration: "none",
                color: "#eaeaea",
                fontweight: "1000",
              }}
              to="/about"
            >
              ABOUT US
            </Link>
            {menu === "About" ? <hr /> : <></>}
          </li>
        </ul>
        <div className="nav-login-cart">
          <div>
            {localStorage.getItem("auth-token") ? (
              <button
                onClick={() => {
                  localStorage.removeItem("auth-token");
                  window.location.replace("/");
                }}
              >
                Logout
              </button>
            ) : (
              <Link to="/Signuplogin">
                <button>Login</button>
              </Link>
            )}
          </div>
          <Link to="/cart">
            <img src={Cart_icon} alt="" className="cart-img" />
          </Link>
          <div className="nav-login-cart-count">{getTotalCartItems()}</div>
        </div>
      </div>
    </>
  );
};

export default Navbar;
