import React, { useState, useContext, useRef } from "react";
import { Link } from "react-router-dom";
import logo_png from "../imgss/icone2.png";
import Cart_icon from "../imgss/cart.png";
import "./Navbar.css";
import { ShopContext } from "../../Context/ShopContext";
import dropdown from "../Assets/dropdown.png";

const Navbar = () => {
  const [menu, setMenu] = useState("home");
  const [isDropdownVisible, setDropdownVisible] = useState(false);

  const { getTotalCartItems } = useContext(ShopContext);
  const menuref = useRef();

  const dropdown_toggle = () => {
    setDropdownVisible(!isDropdownVisible);
  };

  return (
    <div className="navbar">
      <div className="logo">
        <Link to="/">
          <img
            src={logo_png}
            alt=""
            onClick={() => {
              setMenu("home");
            }}
          />
        </Link>
        <p>ShOppi</p>
      </div>
      <img
        src={dropdown}
        alt=""
        className={`dropdown ${isDropdownVisible ? "open" : ""}`}
        onClick={dropdown_toggle}
      />
      <ul
        ref={menuref}
        onClick={dropdown_toggle}
        className={`nav-items ${isDropdownVisible ? "nav-item-visible" : ""}`}
      >
        <li
          onClick={() => {
            setMenu("home");
          }}
        >
          <Link
            to="/"
            style={{
              textDecoration: "none",
              color: "#eaeaea",
              fontWeight: "500",
            }}
          >
            HOME
          </Link>
          {menu === "home" ? <hr /> : <></>}
        </li>
        {/* ... (similar adjustments for other menu items) */}
      </ul>
      <div className="nav-login-cart">
        <div>
          {localStorage.getItem("auth-token") ? (
            <button
              onClick={() => {
                localStorage.removeItem("auth-token");
                window.location.replace("/");
              }}
            >
              Logout
            </button>
          ) : (
            <Link to="/Signuplogin">
              <button>Login</button>
            </Link>
          )}
        </div>
        <Link to="/cart">
          <img src={Cart_icon} alt="" className="cart-img" />
        </Link>
        <div className="nav-login-cart-count">{getTotalCartItems()}</div>
      </div>
    </div>
  );
};

export default Navbar;
