import React from "react";
import "./AboutUs.css";
import logo_png from "../imgss/icone2.png";
const AboutUs = () => {
  return (
    <>
      <div className="aboutUs-heading">
        <div className="aboutUs-logo">
          {" "}
          <img src={logo_png} alt=" " />
          <h1>ShOppi</h1>
        </div>
        <hr></hr>
        <div className="aboutUs-aboutus">
          {" "}
          <h3>About us</h3>
          <p>
            Welcome to ShOppi, your premier destination for stylish watches and
            fashionable clothing for men, women, and kids. At ShOppi, we're
            dedicated to helping you showcase your individuality through our
            carefully curated collection of high-quality products.
          </p>
        </div>
        <div className="aboutUs-ourMission">
          <h3>Our Mission</h3>
          <p>
            At ShOppi, our mission is to redefine online shopping by offering a
            diverse range of trendy watches and clothing that cater to every
            taste and occasion. We're committed to providing an exceptional
            shopping experience by delivering top-notch customer service and
            premium products that elevate your style.
          </p>
        </div>
        .aboutUs
      </div>
    </>
  );
};

export default AboutUs;
