import React, { useState, useContext } from "react";
import { Link } from "react-router-dom";
import logo_png from "../imgss/icone2.png";
import Cart_icon from "../imgss/cart.png";
import "./Navbar.css";
import { ShopContext } from "../../Context/ShopContext";

const Navbar = () => {
  const [menu, setMenu] = useState("home");
  const [showDropdown, setShowDropdown] = useState(false);

  const { getTotalCartItems } = useContext(ShopContext);

  const toggleDropdown = () => {
    setShowDropdown(!showDropdown);
  };

  return (
    <>
      <div className="navbar">
        <div className="logo">
          <Link to="/">
            <img
              src={logo_png}
              alt=" "
              onClick={() => {
                setMenu("home");
              }}
            />
          </Link>
          <p>ShOppi</p>
        </div>
        <div className="dropdown-button" onClick={toggleDropdown}>
          &#9776; Menu
        </div>
        <ul className={`nav-items ${showDropdown ? "show" : ""}`}>
          <li
            onClick={() => {
              setMenu("home");
              setShowDropdown(false);
            }}
          >
            <Link
              style={{
                textDecoration: "none",
                color: "#eaeaea",
                fontWeight: "500",
              }}
              to="/"
            >
              HOME
            </Link>
            {menu === "home" ? <hr /> : <></>}
          </li>
          <li
            onClick={() => {
              setMenu("Clothes");
              setShowDropdown(false);
            }}
          >
            <Link
              to="/Clothes"
              style={{
                textDecoration: "none",
                color: "#eaeaea",
                fontweight: "1000",
              }}
            >
              CLOTHES
            </Link>
            {menu === "Clothes" ? <hr /> : <></>}
          </li>
          <li
            onClick={() => {
              setMenu("Watches");
              setShowDropdown(false);
            }}
          >
            <Link
              style={{
                textDecoration: "none",
                color: "#eaeaea",
                fontweight: "1000",
              }}
              to="/Watches"
            >
              WATCHES
            </Link>
            {menu === "Watches" ? <hr /> : <></>}
          </li>
          <li
            onClick={() => {
              setMenu("About");
              setShowDropdown(false);
            }}
          >
            <Link
              style={{
                textDecoration: "none",
                color: "#eaeaea",
                fontweight: "1000",
              }}
              to="/about"
            >
              ABOUT US
            </Link>
            {menu === "About" ? <hr /> : <></>}
          </li>
        </ul>
        <div className="nav-login-cart">
          <div>
            {localStorage.getItem("auth-token") ? (
              <button
                onClick={() => {
                  localStorage.removeItem("auth-token");
                  window.location.replace("/");
                }}
              >
                Logout
              </button>
            ) : (
              <Link to="/Signuplogin">
                <button>Login</button>
              </Link>
            )}
          </div>

          <Link to="/cart">
            <img src={Cart_icon} alt="" />
          </Link>
          <div className="nav-login-cart-count">{getTotalCartItems()}</div>
        </div>
      </div>
    </>
  );
};

export default Navbar;
