import React, { useState, useEffect, useContext } from "react";
import { Link, useLocation } from "react-router-dom";
import logo_png from "../imgss/icone2.png";
import Cart_icon from "../imgss/cart.png";
import "./Navbar.css";
import { ShopContext } from "../../Context/ShopContext";

const Navbar = () => {
  const location = useLocation();
  const [menu, setMenu] = useState("home");
  const [userName, setUserName] = useState("");
  const { getTotalCartItems } = useContext(ShopContext);
  useEffect(() => {
    // Check if the user is logged in based on the state
    if (location.state && location.state.name) {
      setIsLoggedIn(true);
      setUserName(location.state.name);
    } else {
      setIsLoggedIn(false);
      setUserName("");
    }
  }, [location.state]);

  return (
    <>
      <div className="navbar">
        <div className="logo">
          <Link to="/">
            <img
              src={logo_png}
              alt=" "
              onClick={() => {
                setMenu("home");
              }}
            />
          </Link>
          <p>ShOppi</p>
        </div>
        <ul className="nav-items">
          <li
            onClick={() => {
              setMenu("home");
            }}
          >
            <Link
              style={{
                textDecoration: "none",
                color: "#eaeaea",
                fontWeight: "500",
              }}
              to="/"
            >
              HOME
            </Link>
            {menu === "home" ? <hr /> : <></>}
          </li>
          <li
            onClick={() => {
              setMenu("Clothes");
            }}
          >
            <Link
              to="/Clothes"
              style={{
                textDecoration: "none",
                color: "#eaeaea",
                fontweight: "1000",
              }}
            >
              CLOTHES
            </Link>
            {menu === "Clothes" ? <hr /> : <></>}
          </li>
          <li
            onClick={() => {
              setMenu("Watches");
            }}
          >
            <Link
              style={{
                textDecoration: "none",
                color: "#eaeaea",
                fontweight: "1000",
              }}
              to="/Watches"
            >
              WATCHES
            </Link>
            {menu === "Watches" ? <hr /> : <></>}
          </li>
          <li
            onClick={() => {
              setMenu("About");
            }}
          >
            <Link
              style={{
                textDecoration: "none",
                color: "#eaeaea",
                fontweight: "1000",
              }}
              to="/about"
            >
              ABOUT US
            </Link>
            {menu === "About" ? <hr /> : <></>}
          </li>
        </ul>
        <div className="nav-login-cart">
          <div>
            <Link to="/Signuplogin">
              <button> Logout {userName}</button>
            </Link>
          </div>

          <Link to="/cart">
            <img src={Cart_icon} alt="" />
          </Link>
          <div className="nav-login-cart-count">{getTotalCartItems()}</div>
        </div>
      </div>
    </>
  );
};

export default Navbar;
