import React, { useContext, useState } from "react";
import { ShopContext } from "../Context/ShopContext";
import PropTypes from "prop-types";
import dropdown_icon from "../components/Assets/dropdown_icon.png";
import Item from "../components/item/Item";
import "./CSS/ShopCategory.css";

const ShopCategory = (props) => {
  const { all_product } = useContext(ShopContext);
  const [visibleProduct, setVisibleProduct] = useState(12);
  const [showSortOptions, setShowSortOptions] = useState(false);
  const [selectedSort, setSelectedSort] = useState("default"); // default or "women", "men", "kids"

  const handleLoadMore = () => {
    setVisibleProduct((prevVisibleProduct) => prevVisibleProduct + 12);
  };

  const filteredProducts = all_product.filter(
    (item) => props.category === item.category
  );

  const productCategory = all_product.filter(
    (item) => props.category === item.gender
  );

  const sortProduct = () => {
    switch (selectedSort) {
      case "women":
        return productCategory.filter((item) => item.gender === "women");
      case "men":
        return productCategory.filter((item) => item.gender === "men");
      case "kids":
        return productCategory.filter((item) => item.gender === "kids");
      default:
        return productCategory;
    }
  };

  const handleSort = (sortOption) => {
    setSelectedSort(sortOption);
    setShowSortOptions(false); // close the sort options dropdown
  };

  return (
    <div className="shopCategory">
      <img src={props.banner} className="poster" alt="" />
      <div className="shopcategory-index">
        <p>
          <strong>Showing</strong>
          <span>
            &nbsp; 1-{Math.min(visibleProduct, filteredProducts.length)}
          </span>{" "}
          Out of {filteredProducts.length}
        </p>
        <div
          className="shopcategory-sort"
          onClick={() => setShowSortOptions(!showSortOptions)}
        >
          Sort by <img src={dropdown_icon} alt=" " />
        </div>
        {showSortOptions && (
          <div className="sort-options">
            <div onClick={() => handleSort("women")}>Women</div>
            <div onClick={() => handleSort("men")}>Men</div>
            <div onClick={() => handleSort("kids")}>Kids</div>
          </div>
        )}
      </div>
      <div className="shopcategory-product">
        {sortProduct()
          .slice(0, visibleProduct)
          .map((item, i) => (
            <Item
              key={i}
              id={item.id}
              name={item.name}
              image={item.image}
              new_price={item.new_price}
              old_price={item.old_price}
            />
          ))}
      </div>
      {visibleProduct < sortProduct().length && (
        <div className="shopcategory-loadmore" onClick={handleLoadMore}>
          Explore more
        </div>
      )}
    </div>
  );
};

ShopCategory.propTypes = {
  banner: PropTypes.string.isRequired,
  category: PropTypes.string.isRequired,
};

export default ShopCategory;
